import React from "react";
import { Label, Grupoinput, Input, LeeyendaError, IconoValidacion, expresionRegular} from "./../Elementos/Formulario";
import { faCircleCheck } from '@fortawesome/free-solid-svg-icons'

const InputComponente = ({estado, cambiarEstado, label, placeholder,name, tipo, leeyendaError, expresionRegular}) => {
   
    const onChange= (e) => {
        cambiarEstado({...estado, campo: e.target.value});
    }

    const validacion = () => {
     if(expresionRegular){
         if(expresionRegular.test(estado.campo)) {
            console.log('Input correcto')
      } else{
          console.log('Input incorrecto')
      }
    }
 }
    return (
        <div>
            <Label htmlFor={name}>{label}</Label>
        <Grupoinput>
        <Input type={tipo}
               placeholder={placeholder} 
               id={name}
               value={estado.campo}
               onChange={onChange}
               onKeyUp={validacion}
               onBlur={validacion}

               />
        <IconoValidacion icon={faCircleCheck}/>
        </Grupoinput>
              <LeeyendaError>{leeyendaError}</LeeyendaError>
          </div>
    );
 }
 export default InputComponente ;
