import styled from "styled-components";

        const colores = {
        borde: "0075FF",
        error: "bb2929",
        exito: "1ed12d"
}

        const Formulario = styled.form`
        display: grid;
        grid-template-columns: 1fr 1fr;
        gap: 20px;
        @media (max-width: 800px){
         grid-template-columns: 1fr;
} `;
        const Label = styled.label`
        display: block;
        font-weight: 700;
        padding: 10px;
        min-height: 40px;
        cursor: pointer;

        `;


    const Grupoinput = styled.div`
     position: realative;
     z-index:90;


    `;

    const Input = styled.input`
    width: 100%;
    background: #fff;
    border-radius: 3px;
    height: 45px;
    line-height: 45px;
    padding: 0 40px 10px;
    transition: .3s ease all;
    border: 3px solid trasparent;

    &:focus {
        border: 3px solid ${colores.borde}
        outline: none;
        box-shadow: 3px opx 30px rgba(163,163,163, 0.4);
    }

    `;

export {Formulario, Label, Grupoinput, Input};
