import styled, {css} from "styled-components";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'

        const colores = {
        borde: "#0075FF",
        error: "#bb2929",
        exito: "#1ed12d"
}

        const Formulario = styled.form`
        display: grid;
        grid-template-columns: 1fr 1fr;
        gap: 20px;
        @media (max-width: 800px){
         grid-template-columns: 1fr;
} `;
    const Label = styled.label`
        display: block;
        font-weight: 700;
        padding: 10px;
        min-height: 40px;
        cursor: pointer; 

    ${props => props.valido ==='false' && css`
        color: ${colores.error};
    `}
        `;


    const Grupoinput = styled.div`
        position: relative;
        z-index: 90;


    `;

    const Input = styled.input`
        width: 100%;
        background: #fff;
        border-radius: 3px;
        height: 35  8px;
        line-height: 45px;
        padding: 0 30px 10px;
        transition: .3s ease all;
        border: 3px solid transparent;
        style:text-align:right;
        
        &:focus{
            border: 3px solid ${colores.borde};
            outline: none;
            box-shadow: 3px 0px 30px rgba(163,163,163, 0.4);
        }
        ${props => props.valido === `true`&& css`
         border: 3px solid transparent;
         `}
         ${props => props.valido === `false`&& css`
         border: 3px solid ${colores.error} !important;
         `}
`;   
    const LeeyendaError = styled.p`
        font-size: 12px;
        margin-bottom: 0;
        color: ${colores.error};
        display: none;
          
        ${props => props.valido === 'true' && css`
            display: none;
        `}    
        ${props => props.valido === 'false' && css`
        display: block;
        `}
     
`;

       const IconoValidacion = styled(FontAwesomeIcon)`
            position: absolute;
            right: 10px;
            bottom: 14px;
            z-index: 100;
            font-size: 26px;
            opacity: 0;

            ${props => props.valido === 'false' && css`
            opacity: 1;
            color: ${colores.error};
            `}
            ${props => props.valido === 'true' && css`
            opacity: 1;
            color: ${colores.exito};
            `}
 `;      
        const ContenedordeTerminos = styled.div`
        grid-column: span 2;
         input{
             margin-right: 10px;
         }
`;      

        const ContenedorBotonCentrado = styled.div`
         display: flex;
         flex-direction: column;
         align-items: center;
         grid-column: span 2;  
`;

        const Boton = styled.button`
        width: 30%;
        background: #000;
        border-radius: 3px;
        height: 45px;
        line-height: 45px;
        padding: 0 40px 10px;
        transition: .1s ease all;
        border: none;
        font-height: bold;
        color: #fff;
        cursor: pointer;

        &:hover {
            box-shadow: 3px 0px 30px rgba(163, 163, 163, 1);    
        }

`;

        const MensajedeExito = styled.p`
       font-size: 14px;
       color: ${colores.exito};
       display: none;
`;
        const MensajedeError = styled.div`
        height: 45px;
        line-height: 45px;
        background: ${colores.error};
        padding: opx 15px;
        border-radius: 3px;
        grid-column: span 2;
        p {
            margin: 0;
        }
        b {
            margin-left: 10px;
        }
`;
export { Formulario,
         Label, 
         Grupoinput,
         Input, 
         LeeyendaError, 
         IconoValidacion,
         ContenedordeTerminos,
         ContenedorBotonCentrado,
         Boton,
         MensajedeExito,
         MensajedeError
         };
